##widgets
st.header("s.widgets")
with st.form("input form"):
    name = st.text_input("Enter your name")
    age = st.number_input("Enter your age")
    mood = st.radio("Select your mood",("happy","sad","angry"))
    language = st.multiselect("Select your Languages",("English","hindi","french"))
    submit = st.form_submit_button("Submit")
    if submit:
        st.success(f"Name : {name},Age : {int(age)}, Mood : {mood}, Language : {language}")
        
col1 , col2, col3 = st.columns(3)
with col1:
    st.text_input("Enter your name")
    st.number_input("Enter your age")
with col2:
    st.radio("Select your mood",("happy","sad","happy"))
    st.multiselect("Select your Languages",("English","hindi","french"))
with col3:

  st.title("output")   


